import {Acl, DataResource, RelatedIdentifier, Right, State} from "@/lib/definitions";
import {Tag} from "@kit-data-manager/data-view-web-component";
import qr from 'qr-image';

import {
    descriptionForDataResource, metadataForDataResource,
    rightTextForDataResource,
    subtitleForDataResource, titleForDataResource
} from "@/lib/general/datacard-utils";

export const propertiesForSchemaRecord = (resource: DataResource) => {
    return {
        "dataTitle": titleForDataResource(resource),
        "subTitle": subtitleForDataResource(resource),
        "imageUrl": thumbForMetadataRecord(resource),
        "bodyText": descriptionForDataResource(resource),
        "textRight": rightTextForDataResource(resource),
        "metadata": metadataForDataResource(resource),
        "tags": tagsForSchemaRecord(resource),
    }
}

export const propertiesForMetadataRecord = (resource: DataResource) => {
    return {
        "dataTitle": titleForDataResource(resource),
        "subTitle": subtitleForDataResource(resource),
        "imageUrl": thumbForMetadataRecord(resource),
        "bodyText": descriptionForDataResource(resource),
        "textRight": rightTextForDataResource(resource),
        "metadata": metadataForDataResource(resource),
        "tags": tagsForMetadataRecord(resource),
    }
}

const tagsForSchemaRecord = (resource: DataResource) => {
    //type tag
    let tags: Array<Tag> = new Array<Tag>;
    if (resource.resourceType) {
        const value: string = resource.resourceType.value.substring(0, resource.resourceType.value.indexOf("_"));
        tags.push({
            color: "#90EE90",
            text: value,
            iconName: "fluent-mdl2:shapes",
            tooltip: "The schema type.",
        } as Tag);
    }

    tags.push(stateToTag(resource.state));
    tags.push(getOpenStateTag(resource.acls));
    tags.push(rightsToTag(resource.rights));

    return tags;
}

const tagsForMetadataRecord = (resource: DataResource) => {
    //type tag
    let tags: Array<Tag> = new Array<Tag>;
    if (resource.resourceType) {
        const value: string = resource.resourceType.value.substring(0, resource.resourceType.value.indexOf("_"));
        tags.push({
            color: "#90EE90",
            text: value,
            iconName: "fluent-mdl2:shapes",
            tooltip: "The metadata type.",
        } as Tag);
    }

    tags.push(stateToTag(resource.state));
    tags.push(getOpenStateTag(resource.acls));
    tags.push(rightsToTag(resource.rights));

    //@TODO add link to schema using related identifiers

    const schemaIdentifier: RelatedIdentifier | undefined = resource.relatedIdentifiers.find((identifier) => identifier.relationType === "HAS_METADATA");
    if (schemaIdentifier) {
        const schemaId: string | undefined = schemaIdentifier.value?.substring(schemaIdentifier.value?.lastIndexOf("/") + 1, schemaIdentifier.value?.indexOf("?"));
        const basePath: string = (process.env.NEXT_PUBLIC_BASE_PATH ? process.env.NEXT_PUBLIC_BASE_PATH : "");

        tags.push({
            "color": "#33CCFF",
            "text": schemaId,
            "iconName": "line-md:link",
            "url": `${basePath}/metastore/schemas/${schemaId}/view`,
            "target": "_self",
            "tooltip": "Show schema"
        } as Tag);
    }

    return tags;
}

function rightsToTag(rights: Right[]): Tag {
    //rights tag
    if (rights && rights.length > 0) {
        return {
            "color": "#90EE90",
            "text": "Licensed",
            "iconName": "mynaui:copyright",
            "url": rights[0].schemeUri
        } as Tag;
    } else {
        return {
            color: "#FFCCCB",
            text: "Unlicensed",
            iconName: "mynaui:copyright-slash",
            tooltip: "The resource has no license assigned."
        } as Tag;
    }
}

function getOpenStateTag(acls: Acl[] | undefined): Tag {
    //access tags
    let open = false;
    if (acls && acls.find((acl) => acl.sid === "anonymousUser")) {
        open = true;
    }

    if (open) {
        return {
            color: "#90EE90",
            text: "Open",
            iconName: "zondicons:lock-open",
            tooltip: "The resource is publicly accessible."
        } as Tag;
    }

    return {
        color: "#FFCCCB",
        text: "Protected",
        iconName: "zondicons:lock-closed",
        tooltip: "The resource has access restrictions."
    } as Tag;
}

function stateToTag(state: State | undefined): Tag {
    if (state === "VOLATILE") {
        return {
            color: "#90EE90",
            text: "Volatile",
            iconName: "f7:pin-slash",
            tooltip: "The resource can be modified.",
        } as Tag;
    } else if (state === "FIXED") {
        return {
            color: "yellow",
            text: "Fixed",
            iconName: "f7:pin",
            tooltip: "The resource cannot be modified."
        } as Tag;
    } else if (state === "REVOKED") {
        return {
            color: "#FFD580",
            text: "Revoked",
            iconName: "bytesize:trash",
            tooltip: "The resource is no longer publicly available."
        } as Tag;
    } else if (state === "GONE") {
        return {
            color: "#FFCCCB",
            text: "Gone",
            iconName: "bytesize:trash",
            tooltip: "The resource is no longer available."
        } as Tag;
    }

    return {
        color: "#686868",
        text: "Unknown",
        iconName: "ic:sharp-question-mark",
        tooltip: "The state is unknown."
    } as Tag;
}

export const thumbForMetadataRecord = (resource: DataResource) => {
    if (resource.resourceType.value === "JSON_Schema" || resource.resourceType.value === "JSON_Metadata") {
        return "data:image/png;base64,";
    } else {
        return "data:image/png;base64,";
    }
}

export async function generateBase64QRCode(url) {
    return new Promise<string>((resolve, reject) => {
        try {
            const qrPng = qr.image(url, {type: 'png'});
            const chunks: any[] = [];

            qrPng.on('data', (chunk: any[]) => chunks.push(chunk));
            qrPng.on('end', () => {
                const buffer = Buffer.concat(chunks);
                const base64Png = `data:image/png;base64,${buffer.toString('base64')}`;
                resolve(base64Png);
            });

            qrPng.on('error', (err:any) => reject(err));
        } catch (error) {
            reject(error);
        }
    });
}




